<?php
ini_set('memory_limit', '4024M');
header('Content-Type: text/html; charset=utf-8');
$correos = "flopez@kyp.mx";
$recipients = explode(',', $correos);
//$recipients = array('flopez@opk.mx','flopez.loya@gmail.com');
/**
 * This example shows making an SMTP connection with authentication.
 */

//SMTP needs accurate times, and the PHP time zone MUST be set
//This should be done in your php.ini, but this is how to do it if you don't have access to that
date_default_timezone_set("America/Mexico_City");
require 'PHPMailerAutoload.php';

//Create a new PHPMailer instance
$mail = new PHPMailer;
//Tell PHPMailer to use SMTP
$mail->isSMTP();
$mail->CharSet = 'UTF-8';
//Enable SMTP debugging
// 0 = off (for production use)
// 1 = client messages
// 2 = client and server messages
$mail->SMTPDebug = 2;

//Ask for HTML-friendly debug output
$mail->Debugoutput = 'html';

//Set the hostname of the mail server
$mail->Host = 'server.kyplearning.com';
// use
// $mail->Host = gethostbyname('smtp.gmail.com');
// if your network does not support SMTP over IPv6

//Set the SMTP port number - 587 for authenticated TLS, a.k.a. RFC4409 SMTP submission
$mail->Port = 465;

//Set the encryption system to use - ssl (deprecated) or tls
$mail->SMTPSecure = 'tls';

//Whether to use SMTP authentication 
$mail->SMTPAuth = true;
//Username to use for SMTP authentication
$mail->Username = "admin@kyplearning.com";
//Password to use for SMTP authentication
$mail->Password = "Renovatio1";
//Set who the message is to be sent from
$mail->FromName = "Seguridad de cuentas KYP";
$mail->setFrom('security@kyp.mx', 'Seguridad de cuentas KYP');
//Set an alternative reply-to address
$mail->addReplyTo('security@kyp.mx', 'Seguridad de cuentas KYP');
//Set who the message is to be sent to
//$mail->addAddress('flopez@opk.mx,flopez.loya@gmail.com', 'John Doe');

foreach($recipients as $email)
{
   $mail->AddBCC($email);
}

$message = '<img src="data:image/png;base64,"><hr><h2>Hola #USER,</h2><hr><p>Alguien intento ingresar a tu cuenta sin autorización. Por tu seguridad bloqueamos tu cuenta, para activar tu cuenta es necesario que cambies tu contraseña en la siguiente liga: https://kyplearning.com</p><br><html><head><meta http-equiv="Content-Type" content="text/html; charset=gb18030"><meta name=3D"viewport" content=3D"widt=
"></body></html>';
$contenido = $message;
$mensaje = "test";
$asunto ="Intento de inicio sesión en tu cuenta.";
//Set the subject line
$mail->Subject = $asunto;
//Read an HTML message body from an external file, convert referenced images to embedded,
//convert HTML into a basic plain-text alternative body
$mensaje = $contenido;
$mail->msgHTML($mensaje);
//Replace the plain text body with one created manually
$mail->AltBody = $mensaje;
//Attach an image file
//$mail->addAttachment('images/phpmailer_mini.png');

//send the message, check for errors
if (!$mail->send()) {
    echo "Message sent!";
} else {
    echo "ERROR";
}
